import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { io } from 'socket.io-client';
import { setCurrentPoll, setPollResults, endPoll, addPastPoll } from '../store/pollSlice';
import { addMessage, toggleChat } from '../store/chatSlice';
import { ChatIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const socket = io(process.env.REACT_APP_SERVER_URL || 'http://localhost:5000');

const TeacherDashboard = () => {
  const dispatch = useDispatch();
  const { currentPoll, pollResults, isPollActive } = useSelector((state) => state.poll);
  const { isChatOpen, messages } = useSelector((state) => state.chat);
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState(['', '']);
  const [timeLimit, setTimeLimit] = useState(60);
  const [connectedStudents, setConnectedStudents] = useState([]);
  const [chatMessage, setChatMessage] = useState('');

  useEffect(() => {
    socket.emit('teacher:join');

    socket.on('student:joined', (student) => {
      setConnectedStudents((prev) => [...prev, student]);
    });

    socket.on('student:left', (student) => {
      setConnectedStudents((prev) => prev.filter((s) => s.id !== student.id));
    });

    socket.on('poll:answer', (answer) => {
      // Handle student answers
    });

    socket.on('chat:message', (message) => {
      dispatch(addMessage(message));
    });

    return () => {
      socket.off('student:joined');
      socket.off('student:left');
      socket.off('poll:answer');
      socket.off('chat:message');
    };
  }, [dispatch]);

  const handleCreatePoll = (e) => {
    e.preventDefault();
    if (!question.trim() || options.some(opt => !opt.trim())) return;

    const pollData = {
      question,
      options: options.map(opt => ({ text: opt, votes: 0 })),
      timeLimit,
    };

    socket.emit('poll:create', pollData);
    dispatch(setCurrentPoll(pollData));
    setQuestion('');
    setOptions(['', '']);
  };

  const handleAddOption = () => {
    setOptions([...options, '']);
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  const handleEndPoll = () => {
    socket.emit('poll:end');
    dispatch(endPoll());
    if (pollResults) {
      dispatch(addPastPoll({ ...currentPoll, results: pollResults }));
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!chatMessage.trim()) return;

    socket.emit('chat:message', chatMessage);
    setChatMessage('');
  };

  const chartData = pollResults ? {
    labels: pollResults.options.map(opt => opt.text),
    datasets: [
      {
        label: 'Votes',
        data: pollResults.options.map(opt => opt.votes),
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
        borderColor: 'rgb(59, 130, 246)',
        borderWidth: 1,
      },
    ],
  } : null;

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Teacher Dashboard</h1>
          <button
            onClick={() => dispatch(toggleChat())}
            className="p-2 rounded-full hover:bg-gray-200"
          >
            <ChatIcon className="h-6 w-6" />
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-4">Create New Poll</h2>
            <form onSubmit={handleCreatePoll} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Question</label>
                <input
                  type="text"
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  placeholder="Enter your question"
                  disabled={isPollActive}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Options</label>
                {options.map((option, index) => (
                  <input
                    key={index}
                    type="text"
                    value={option}
                    onChange={(e) => handleOptionChange(index, e.target.value)}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    placeholder={`Option ${index + 1}`}
                    disabled={isPollActive}
                  />
                ))}
                <button
                  type="button"
                  onClick={handleAddOption}
                  className="mt-2 text-blue-500 hover:text-blue-600"
                  disabled={isPollActive}
                >
                  + Add Option
                </button>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Time Limit (seconds)</label>
                <input
                  type="number"
                  value={timeLimit}
                  onChange={(e) => setTimeLimit(Number(e.target.value))}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  min="10"
                  max="300"
                  disabled={isPollActive}
                />
              </div>

              <button
                type="submit"
                className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
                disabled={isPollActive}
              >
                Create Poll
              </button>
            </form>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-4">Live Results</h2>
            {isPollActive && (
              <div className="mb-4">
                <h3 className="text-lg font-medium">{currentPoll.question}</h3>
                {chartData && (
                  <div className="mt-4">
                    <Bar data={chartData} />
                  </div>
                )}
                <button
                  onClick={handleEndPoll}
                  className="mt-4 w-full bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 transition"
                >
                  End Poll
                </button>
              </div>
            )}
          </div>
        </div>

        {isChatOpen && (
          <div className="fixed bottom-4 right-4 w-80 bg-white rounded-lg shadow-lg">
            <div className="p-4 border-b">
              <div className="flex justify-between items-center">
                <h3 className="font-semibold">Chat</h3>
                <button onClick={() => dispatch(toggleChat())}>
                  <XMarkIcon className="h-5 w-5" />
                </button>
              </div>
            </div>
            <div className="h-64 overflow-y-auto p-4">
              {messages.map((msg, index) => (
                <div key={index} className="mb-2">
                  <span className="font-semibold">{msg.sender}: </span>
                  <span>{msg.text}</span>
                </div>
              ))}
            </div>
            <form onSubmit={handleSendMessage} className="p-4 border-t">
              <div className="flex space-x-2">
                <input
                  type="text"
                  value={chatMessage}
                  onChange={(e) => setChatMessage(e.target.value)}
                  className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  placeholder="Type a message..."
                />
                <button
                  type="submit"
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                  Send
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default TeacherDashboard;
