import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { io } from 'socket.io-client';
import { setCurrentPoll, setPollResults, endPoll, addPastPoll } from '../store/pollSlice';
import { addMessage, toggleChat } from '../store/chatSlice';
import { ChatBubbleLeftIcon, XMarkIcon, UserGroupIcon, ClockIcon } from '@heroicons/react/24/outline';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const socket = io(process.env.REACT_APP_SERVER_URL || 'http://localhost:5000');

const TeacherDashboard = () => {
  const dispatch = useDispatch();
  const { currentPoll, pollResults, isPollActive } = useSelector((state) => state.poll);
  const { isChatOpen, messages } = useSelector((state) => state.chat);
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState(['', '']);
  const [timeLimit, setTimeLimit] = useState(60);
  const [connectedStudents, setConnectedStudents] = useState([]);
  const [chatMessage, setChatMessage] = useState('');

  useEffect(() => {
    socket.emit('teacher:join');

    socket.on('student:joined', (student) => {
      setConnectedStudents((prev) => [...prev, student]);
    });

    socket.on('student:left', (student) => {
      setConnectedStudents((prev) => prev.filter((s) => s.id !== student.id));
    });

    socket.on('poll:answer', (answer) => {
      // Handle student answers
    });

    socket.on('chat:message', (message) => {
      dispatch(addMessage(message));
    });

    return () => {
      socket.off('student:joined');
      socket.off('student:left');
      socket.off('poll:answer');
      socket.off('chat:message');
    };
  }, [dispatch]);

  const handleCreatePoll = (e) => {
    e.preventDefault();
    if (!question.trim() || options.some(opt => !opt.trim())) return;

    const pollData = {
      question,
      options: options.map(opt => ({ text: opt, votes: 0 })),
      timeLimit,
    };

    socket.emit('poll:create', pollData);
    dispatch(setCurrentPoll(pollData));
    setQuestion('');
    setOptions(['', '']);
  };

  const handleAddOption = () => {
    setOptions([...options, '']);
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  const handleEndPoll = () => {
    socket.emit('poll:end');
    dispatch(endPoll());
    if (pollResults) {
      dispatch(addPastPoll({ ...currentPoll, results: pollResults }));
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!chatMessage.trim()) return;

    socket.emit('chat:message', chatMessage);
    setChatMessage('');
  };

  const chartData = pollResults ? {
    labels: pollResults.options.map(opt => opt.text),
    datasets: [
      {
        label: 'Votes',
        data: pollResults.options.map(opt => opt.votes),
        backgroundColor: 'rgba(99, 102, 241, 0.5)',
        borderColor: 'rgb(99, 102, 241)',
        borderWidth: 1,
      },
    ],
  } : null;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Teacher Dashboard</h1>
            <p className="text-gray-600">Create and manage live polls</p>
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex items-center text-gray-600">
              <UserGroupIcon className="h-5 w-5 mr-2" />
              <span>{connectedStudents.length} Students</span>
            </div>
            <button
              onClick={() => dispatch(toggleChat())}
              className="p-2 rounded-full hover:bg-gray-200 text-gray-600"
            >
              <ChatBubbleLeftIcon className="h-6 w-6" />
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white rounded-2xl shadow-sm p-6">
            <h2 className="text-xl font-semibold mb-6">Create New Poll</h2>
            <form onSubmit={handleCreatePoll} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Question</label>
                <input
                  type="text"
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                  placeholder="Enter your question"
                  disabled={isPollActive}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Options</label>
                {options.map((option, index) => (
                  <input
                    key={index}
                    type="text"
                    value={option}
                    onChange={(e) => handleOptionChange(index, e.target.value)}
                    className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 mb-2"
                    placeholder={`Option ${index + 1}`}
                    disabled={isPollActive}
                  />
                ))}
                <button
                  type="button"
                  onClick={handleAddOption}
                  className="text-indigo-600 hover:text-indigo-700 text-sm font-medium"
                  disabled={isPollActive}
                >
                  + Add Option
                </button>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Time Limit (seconds)</label>
                <div className="relative">
                  <input
                    type="number"
                    value={timeLimit}
                    onChange={(e) => setTimeLimit(Number(e.target.value))}
                    className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    min="10"
                    max="300"
                    disabled={isPollActive}
                  />
                  <ClockIcon className="h-5 w-5 text-gray-400 absolute right-3 top-3.5" />
                </div>
              </div>

              <button
                type="submit"
                className="w-full bg-indigo-600 text-white py-3 px-4 rounded-xl hover:bg-indigo-700 transition duration-200"
                disabled={isPollActive}
              >
                Create Poll
              </button>
            </form>
          </div>

          <div className="bg-white rounded-2xl shadow-sm p-6">
            <h2 className="text-xl font-semibold mb-6">Live Results</h2>
            {isPollActive ? (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">{currentPoll.question}</h3>
                {chartData && (
                  <div className="mt-4">
                    <Bar data={chartData} />
                  </div>
                )}
                <button
                  onClick={handleEndPoll}
                  className="mt-6 w-full bg-red-600 text-white py-3 px-4 rounded-xl hover:bg-red-700 transition duration-200"
                >
                  End Poll
                </button>
              </div>
            ) : (
              <div className="text-center text-gray-500 py-12">
                No active poll. Create a new poll to see results.
              </div>
            )}
          </div>
        </div>

        {isChatOpen && (
          <div className="fixed bottom-4 right-4 w-96 bg-white rounded-2xl shadow-lg">
            <div className="p-4 border-b">
              <div className="flex justify-between items-center">
                <h3 className="font-semibold text-gray-900">Chat</h3>
                <button onClick={() => dispatch(toggleChat())} className="text-gray-500 hover:text-gray-700">
                  <XMarkIcon className="h-5 w-5" />
                </button>
              </div>
            </div>
            <div className="h-96 overflow-y-auto p-4">
              {messages.map((msg, index) => (
                <div key={index} className="mb-4">
                  <div className="font-medium text-gray-900">{msg.sender}</div>
                  <div className="text-gray-700">{msg.text}</div>
                </div>
              ))}
            </div>
            <form onSubmit={handleSendMessage} className="p-4 border-t">
              <div className="flex space-x-2">
                <input
                  type="text"
                  value={chatMessage}
                  onChange={(e) => setChatMessage(e.target.value)}
                  className="flex-1 px-4 py-2 rounded-xl border border-gray-300 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                  placeholder="Type a message..."
                />
                <button
                  type="submit"
                  className="bg-indigo-600 text-white px-4 py-2 rounded-xl hover:bg-indigo-700 transition duration-200"
                >
                  Send
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default TeacherDashboard;
