import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { io } from 'socket.io-client';
import { setCurrentPoll, setPollResults, updateTimeRemaining } from '../store/pollSlice';
import { addMessage, toggleChat } from '../store/chatSlice';
import { ChatBubbleLeftIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const socket = io(process.env.REACT_APP_SERVER_URL || 'http://localhost:5000');

const StudentDashboard = () => {
  const dispatch = useDispatch();
  const { currentPoll, pollResults, isPollActive, timeRemaining } = useSelector((state) => state.poll);
  const { isChatOpen, messages } = useSelector((state) => state.chat);
  const { name } = useSelector((state) => state.user);
  const [selectedOption, setSelectedOption] = useState(null);
  const [hasAnswered, setHasAnswered] = useState(false);
  const [chatMessage, setChatMessage] = useState('');

  useEffect(() => {
    socket.emit('student:join', name);

    socket.on('poll:new', (pollData) => {
      dispatch(setCurrentPoll(pollData));
      setSelectedOption(null);
      setHasAnswered(false);
    });

    socket.on('poll:results', (results) => {
      dispatch(setPollResults(results));
    });

    socket.on('chat:message', (message) => {
      dispatch(addMessage(message));
    });

    return () => {
      socket.off('poll:new');
      socket.off('poll:results');
      socket.off('chat:message');
    };
  }, [dispatch, name]);

  useEffect(() => {
    let timer;
    if (isPollActive && !hasAnswered && timeRemaining > 0) {
      timer = setInterval(() => {
        dispatch(updateTimeRemaining(timeRemaining - 1));
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isPollActive, hasAnswered, timeRemaining, dispatch]);

  const handleAnswerSubmit = (option) => {
    if (hasAnswered) return;

    setSelectedOption(option);
    setHasAnswered(true);
    socket.emit('poll:answer', option);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!chatMessage.trim()) return;

    socket.emit('chat:message', chatMessage);
    setChatMessage('');
  };

  const chartData = pollResults ? {
    labels: pollResults.options.map(opt => opt.text),
    datasets: [
      {
        label: 'Votes',
        data: pollResults.options.map(opt => opt.votes),
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
        borderColor: 'rgb(59, 130, 246)',
        borderWidth: 1,
      },
    ],
  } : null;

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Student Dashboard</h1>
          <div className="flex items-center space-x-4">
            <span className="text-gray-600">Welcome, {name}</span>
            <button
              onClick={() => dispatch(toggleChat())}
              className="p-2 rounded-full hover:bg-gray-200"
            >
              <ChatBubbleLeftIcon className="h-6 w-6" />
            </button>
          </div>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          {isPollActive ? (
            <div>
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">{currentPoll.question}</h2>
                {!hasAnswered && (
                  <span className="text-sm text-gray-500">
                    Time remaining: {timeRemaining}s
                  </span>
                )}
              </div>

              {!hasAnswered ? (
                <div className="space-y-4">
                  {currentPoll.options.map((option, index) => (
                    <button
                      key={index}
                      onClick={() => handleAnswerSubmit(option.text)}
                      className={`w-full p-4 text-left rounded-lg border ${
                        selectedOption === option.text
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-blue-500'
                      }`}
                    >
                      {option.text}
                    </button>
                  ))}
                </div>
              ) : (
                <div className="mt-4">
                  <h3 className="text-lg font-medium mb-4">Results</h3>
                  {chartData && (
                    <div className="mt-4">
                      <Bar data={chartData} />
                    </div>
                  )}
                </div>
              )}
            </div>
          ) : (
            <div className="text-center text-gray-500">
              Waiting for the teacher to start a poll...
            </div>
          )}
        </div>

        {isChatOpen && (
          <div className="fixed bottom-4 right-4 w-80 bg-white rounded-lg shadow-lg">
            <div className="p-4 border-b">
              <div className="flex justify-between items-center">
                <h3 className="font-semibold">Chat</h3>
                <button onClick={() => dispatch(toggleChat())}>
                  <XMarkIcon className="h-5 w-5" />
                </button>
              </div>
            </div>
            <div className="h-64 overflow-y-auto p-4">
              {messages.map((msg, index) => (
                <div key={index} className="mb-2">
                  <span className="font-semibold">{msg.sender}: </span>
                  <span>{msg.text}</span>
                </div>
              ))}
            </div>
            <form onSubmit={handleSendMessage} className="p-4 border-t">
              <div className="flex space-x-2">
                <input
                  type="text"
                  value={chatMessage}
                  onChange={(e) => setChatMessage(e.target.value)}
                  className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  placeholder="Type a message..."
                />
                <button
                  type="submit"
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                  Send
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
